import React from 'react';
import { Button, Dialog, DialogActions, DialogContent, DialogContentText, DialogTitle } from '@material-ui/core/';
import DeleteIcon from '@material-ui/icons/Delete';



const ConfirmDelete = ({onDelete,name}) => {
  const [open, setOpen] = React.useState(false);

  const handleClickOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };

  const handleDelete = async (id)=>{
    try {
      const res = await api.delete(`/members/delete/${id}`)

      const dataDelete = [...riders];
      const afterDelete = dataDelete.filter(rider => rider.member_id !==id);
      setRiders([...afterDelete]);
      if (res.status == 200) {
        toast.success(res.data)
      }

    } catch (error) {
      console.log("error occured=>", error)
      toast.error(error)
    }

  }

  return (
    <div>
      {/* <Button variant="outlined" color="primary" onClick={handleClickOpen}>
        Open alert dialog
      </Button> */}
      <DeleteIcon  onClick={handleClickOpen} style={{ cursor: 'pointer', color: 'red' }}/>
      <Dialog

        open={open}
        onClose={handleClose}
        aria-labelledby="alert-dialog-title"
        aria-describedby="alert-dialog-description"
      >
        <DialogTitle id="alert-dialog-title"   style={{marginTop:'5%'}}>{"Delete Sacco Member"}</DialogTitle>
        <DialogContent>
          <DialogContentText id="alert-dialog-description">
                Are you sure you want to delete {name}?
          </DialogContentText>
        </DialogContent>
        <DialogActions style={{ marginBottom:'5%'}} >
          <Button onClick={onDelete} color="primary">
           Yes
          </Button>
          <Button onClick={handleClose} color="primary" autoFocus>
           No
          </Button>
        </DialogActions>
      </Dialog>
    </div>
  );
}

export default ConfirmDelete
