import React from 'react';
import{ Avatar, Button, TextField, FormControlLabel, Checkbox, CssBaseline} from '@material-ui/core';
import Link from '@material-ui/core/Link';
import Paper from '@material-ui/core/Paper';
import Box from '@material-ui/core/Box';
import Grid from '@material-ui/core/Grid';
import LockOutlinedIcon from '@material-ui/icons/LockOutlined';
import Typography from '@material-ui/core/Typography';
import { toast } from 'react-toastify';
import { makeStyles } from '@material-ui/core/styles';
import {withRouter, useHistory} from 'react-router-dom';

import axios from 'axios';


const api = axios.create({
    baseURL: `http://localhost:4000`
})

const useStyles = makeStyles((theme) => ({
  root: {
    height: '100vh',
  },
  image: {
    backgroundImage: 'url(https://source.unsplash.com/random)',
    backgroundRepeat: 'no-repeat',
    backgroundColor:
      theme.palette.type === 'light' ? theme.palette.grey[50] : theme.palette.grey[900],
    backgroundSize: 'cover',
    backgroundPosition: 'center',
  },
  paper: {
    margin: theme.spacing(15, 4),
    // [theme.breakpoints.down('sm')]:{
    //     margin: theme.spacing(15, 4)
    // },
    // [theme.breakpoints.down('md')]:{
    //     margin: theme.spacing(30, 4)
    // },
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
  },
  avatar: {
    marginBottom: theme.spacing(3),
    backgroundColor: theme.palette.secondary.main,
  },
  form: {
    width: '55%', // Fix IE 11 issue.
    [theme.breakpoints.down('sm')]:{
        width: '100%'
    },
    [theme.breakpoints.down('md')]:{
        width: '95%'
    },
    marginTop: theme.spacing(1),
  },
  submit: {
    margin: theme.spacing(3, 0, 2),
    height:'50px'
  },
}));

const [inputs, setInputs] = useState({
  email: "",
  password: "",
  name: ""
});

const { email, password, name } = inputs;

const onChange = e =>
  setInputs({ ...inputs, [e.target.name]: e.target.value });

const onSubmitForm = async e => {
  e.preventDefault();
  try {
    const body = { email, password, name };
    const response = await fetch(
      "http://localhost:4000/admin/register",
      {
        method: "POST",
        headers: {
          "Content-type": "application/json"
        },
        body: JSON.stringify(body)
      }
    );
    const res= await response.json();
      console.log(res)
    if (res.Token) {
      localStorage.setItem("token", res.Token);
      // setAuth(true);
      toast.success("Register Successfully");
    } else {
      setAuth(false);
      toast.error(res);
    }
  } catch (err) {
    console.error(err.message);
  }
};


const Login =()=> {
  const classes = useStyles();
  const history = useHistory();


  return (
    <Grid container component="main" className={classes.root}>
      <CssBaseline />
      <Grid item xs={false} sm={4} md={5} className={classes.image} />
      <Grid item xs={12} sm={8} md={7} component={Paper} elevation={6} square>
        <div className={classes.paper}>
          <Avatar className={classes.avatar}>
            <LockOutlinedIcon />
          </Avatar>
          <Typography component="h1" variant="h5" style={{ marginBottom:'3%'}}>
            Admin Login
          </Typography>
          <form className={classes.form} onClick={formik.handleSubmit}>
            <TextField
              variant="outlined"
              margin="normal"
              // required
              fullWidth
              id="email"
              label="Email Address"
              name="email"
              // autoComplete="email"
              value={formik.values.email}
              onChange={formik.handleChange}
              error={formik.touched.email && Boolean(formik.errors.email)}
              helperText={formik.touched.email && formik.errors.email}
              autoFocus
            />
            <TextField
              variant="outlined"
              margin="normal"
              // required
              fullWidth
              name="password"
              label="Password"
              type="password"
              id="password"
              value={formik.values.password}
              onChange={formik.handleChange}
              error={formik.touched.password && Boolean(formik.errors.password)}
              helperText={formik.touched.password && formik.errors.password}
              // autoComplete="current-password"
            />
            {/* <FormControlLabel
              control={<Checkbox value="remember" color="primary" />}
              label="Remember me"
            /> */}
            <Button
              type="submit"
              fullWidth
              variant="contained"
              color="primary"
              disabled={formik.isSubmitting}
              className={classes.submit}
            >
              Login
            </Button>
            <Grid container>
              <Grid item xs>
                <Link href="#" variant="body2">
                  Forgot password?
                </Link>
              </Grid>
              {/* <Grid item style={{display:'none'}}>
                <Link href="#" variant="body2">
                  {"Don't have an account? Sign Up"}
                </Link>
              </Grid> */}
            </Grid>
          </form>
        </div>
      </Grid>
    </Grid>
  );
}
export default Login;
