import React, { useEffect, useState } from "react";
import { Container } from "reactstrap";
import InfoCards from "../new/InfoCards";
import Charts from "../new/Charts";
import Breadcrumb from "../../layout/breadcrumb";
import Layout from "../AppWrapper"
import axios from 'axios'


const api = axios.create({
  baseURL:`http://localhost:4000`
})

export default function Dashboard() {

  const [user, setUser] = useState(null)
  const [token, setToken] = useState('')

  // const jwt_token = localStorage.tokenated
  const getProfile = async () => {
    setToken(localStorage.tokenated)
    console.log("Stored token=>",token)
    try {
      const res = await Axios.get("http://localhost:4000/admin/is-verified", {
        headers: { token: token }
      });

      // const data = await res.json();
      console.log("data from server==>", res.data)
      setUser(res.data);
      // console.log("Async  user==>", user)
    } catch (err) {
      console.error(err.message);
    }
  };

  useEffect(() => {

    getProfile()

    // setToken(localStorage.tokenated)
    console.log("Token consoled=>", token)
    console.log("User data binding==>", user)

  }, [token])


  return (
    <Layout>

    <Container fluid={true}>
      <Breadcrumb parent="Dashboard" title="Admin Dashboard" />
      {/* <p>Welcome {name}</p> */}
      <InfoCards />
      <Charts/>
    </Container>
    </Layout>
  );
}
