import React, { Fragment, useEffect, useState } from 'react';
import ThemeCustomizer from "../layout/theme-customizer"
import Layout from "./AppWrapper"
import {ToastContainer} from 'react-toastify';
import { BrowserRouter as Router, Route, Switch, Redirect} from "react-router-dom";
import Dashboard from "../components/dashboard/dashboard"
import Login from "../components/new/Login"
import Members from "../components/new/MembersTable"
import Saccos from "../components/new/SaccoTable"
import MembersRegistration from "../components/new/Formik/SaccoMemberRegistration"
import SaccoRegistration from "../components/new/Formik/SaccoRegistration"
import Axios from 'axios';



const App = () => {
  const [user, setUser] = useState(false)
  const [token, setToken] = useState('')

  const jwt_token = localStorage.tokenated
  const getProfile = async () => {

    try {
      const res = await Axios.get("http://localhost:4000/admin/is-verified", {
        headers: { token: jwt_token}
      });

      // const data = await res.json();
      // console.log("data from server==>", res.data)
      setUser(res.data);
    } catch (err) {
      console.error(err.message);
    }
  };

  useEffect(() => {

    getProfile()

    setToken(localStorage.tokenated)
    console.log("Token consoled=>", token )

  }, [token])

  console.log("User data binding==>", user)

  return (
    <Fragment>
      {/* <Layout /> */}

      <Router basename={'/'}>
        <Switch>

           <Route
              exact
              path="/login"
              render={() =>
                token !== null || user !== undefined ? (
                  <Login/>
                ) : (
                  <Redirect to="/dashboard" />
                )
              }
            />
            <Route
              exact
              path="/dashboard"
              render={() =>
                token ? (
                  <Dashboard />
                ) : (
                  <Redirect to="/login" />
                )
              }
            />
            <Route
              exact
              path="/dashboard/saccos"
              render={() =>
                token ? (
                  <Saccos />
                ) : (
                  <Redirect to="/login" />
                )
              }
            />
             <Route
              exact
              path="/dashboard/memberss"
              render={() =>
                token ? (
                  <Members />
                ) : (
                  <Redirect to="/login" />
                )
              }
            />
             <Route
              exact
              path="/dashboard/members/new"
              render={() =>
                token ? (
                  <MembersRegistration />
                ) : (
                  <Redirect to="/login" />
                )
              }
            />
            <Route
              exact
              path="/dashboard/saccoss/new"
              render={() =>
                token ? (
                  <SaccoRegistration />
                ) : (
                  <Redirect to="/login" />
                )
              }
            />
          <Route exact path='/' render={()=> <Redirect to ="/dashboard"/>}/>  
          {/* <Route exact path='/dashboard' component={Dashboard}/>  */}
          {/* <Route exact path='/dashboard/saccos' component={Saccos}/> 
          <Route exact path='/dashboard/members' component={Members}/> 
          <Route exact path='/dashboard/members/new' component={MembersRegistration}/> 
          <Route exact path='/dashboard/saccos/new' component={SaccoRegistration}/>  */}
        </Switch>
      </Router>
      <ToastContainer/>
      <ThemeCustomizer/>
    </Fragment>
  );
}
export default App;
