import React, { Fragment,useState,useEffect } from 'react'
import ReactDOM from 'react-dom';
import './index.scss';
import {firebase_app, auth0} from './data/config';
import {BrowserRouter,Switch,Route,Redirect} from 'react-router-dom'
import * as serviceWorker from './serviceWorker';
import { Provider } from 'react-redux';
import store from './store'
import App from './components/app'
import { CSSTransition,TransitionGroup } from 'react-transition-group'
import {ToastContainer} from 'react-toastify';
import {routes} from './route';
import ConfigDB  from './data/customizer/config'
import {configureFakeBackend ,handleResponse} from './services/fack.backend'
// import {authHeader} from './services/auth'
// Signin page
import Signin from './auth/signin'

// Dashboard routes                                   
import MembersTable from "../src/components/new/MembersTable";
import SaccoTable from "../src/components/new/SaccoTable";
import SaccoMemberRegistration from "../src/components/new/Formik/SaccoMemberRegistration";
import SaccoRegistration from "../src/components/new/Formik/SaccoRegistration";
import Login from "../src/components/new/Login";


// Maintenanc
import Maintenance from "./pages/maintenance"

// import LoginMUI from "../src/components/new/Login"
import Callback from './auth/callback'
import Dashboard from './components/dashboard/dashboard';

// setup fake backend
// configureFakeBackend();


const Root = (props) =>  {

  // const [animate, setAnimate] = useState("");
  // const animation = localStorage.getItem("animation") || ConfigDB.data.router_animation || 'zoom fade'
  const abortController = new AbortController();

  const [authenticated,setAuthenticated] = useState(false)
  // const jwt_token = localStorage.getItem('token');
  const jwt_token =localStorage.getItem('tokenated');


  const checkAuth =  async ()=>{

    console.log("Token==>",localStorage.tokenated)
    const res = await fetch("http://localhost:4000/admin/is-verified", {
      method: "GET",
      headers: { token: localStorage.tokenated }
    });

    const data = await res.json()
    data === true ? setAuthenticated(true) : setAuthenticated(false);
    // console.log("Data from server==>",data)
    setAuthenticated(data);
    // console.log("Async IIFE ==>", authenticated);
    // return data;
    return authenticated;
  }


 const checkToken = ()=>{
  return console.log("Token==>",localStorage.tokenated)
 }

 const setAuth = boolean => {
  setAuthenticated(boolean);
};


  useEffect(() => {

      // setAnimate(animation)
      // console.ignoredYellowBox = ["Warning: Each", "Warning: Failed"];
      // console.disableYellowBox = true;

      (async ()=>{
        const auth = await checkAuth()
        // console.log("Async IIFE==>", auth);
        console.log("Async IIFE authenticated==>", auth);
      })()
      checkAuth()

      // checkToken()

      // return function cleanup() {
      //     abortController.abort();
      // }

      // eslint-disable-next-line 
    }, []);

//  console.log("Authenticated==>",authenticated)
//  console.log("Current user==>",currentUser)

    return(
      <Fragment>
        {/* <Auth0Provider domain={auth0.domain} clientId={auth0.clientId} redirectUri={auth0.redirectUri}> */}
        <Provider store={store}>
        <ToastContainer />
        <BrowserRouter basename={`/dashboard/`}>
        <Switch>

          <Route  path={`non-server/login`} component={Signin} />
          <Route  path={`/login`} component={Login} />

            <App>

                {/* <Route exact path='/' render={() => {
                    return (<Redirect to={`/dashboard/`} />)
                }} />  */}

                <Route
                  exact
                  path="/login"
                  render={props =>
                    !authenticated ? (
                      <Login {...props} setAuth={setAuth} />
                    ) : (
                      <Redirect to="/dashboard/" />
                    )
                  }
                />

                <Route
                 exact
                 path="/dashboard/"
                 render={props =>
                   authenticated ? (
                     <Dashboard {...props} setAuth={setAuth} />
                   ) : (
                     <Redirect to="/login" />
                   )
                 }
                />

                <Route
                  exact
                  path="/dashboard/members"
                  render={props =>
                    authenticated ? (
                      <MembersTable {...props} setAuth={setAuth} />
                    ) : (
                      <Redirect to="/login" />
                    )
                  }
                />

                <Route
                  exact
                  path="/dashboard/members/new"
                  render={props =>
                    authenticated ? (
                      <SaccoMemberRegistration {...props} setAuth={setAuth} />
                    ) : (
                      <Redirect to="/login" />
                    )
                  }
                />


                <Route
                  exact
                  path="/dashboard/saccos/new"
                  render={props =>
                    authenticated ? (
                      <SaccoRegistration {...props} setAuth={setAuth} />
                    ) : (
                      <Redirect to="/login" />
                    )
                  }
                />
                <Route
                  exact
                  path="/dashboard/saccos"
                  render={props =>
                    authenticated ? (
                      <SaccoTable {...props} setAuth={setAuth} />
                    ) : (
                      <Redirect to="/login" />
                    )
                  }
                />




            </App>

        </Switch>
        </BrowserRouter>
        </Provider>
        {/* </Auth0Provider> */}
      </Fragment>
      )
}

ReactDOM.render(<Root/>,
  document.getElementById('root')
);
serviceWorker.unregister();
