  const [authenticated,setAuthenticated] = useState(false)
  // const jwt_token = localStorage.getItem('token');
  const jwt_token =localStorage.getItem('tokenated');


  const checkAuth =  async ()=>{

    console.log("Token==>",localStorage.tokenated)
    const res = await fetch("http://localhost:4000/admin/is-verified", {
      method: "GET",
      headers: { token: localStorage.tokenated }
    });

    const data = await res.json()
    // console.log("Data from server==>",data)
    setAuthenticated(data);
    // console.log("Async IIFE ==>", authenticated);
    // return data;
    return authenticated;
  }


