        <ToastContainer />
        <BrowserRouter basename={`/`}>
        <Switch>

          <Route  path={`non-server/login`} component={Signin} />
          <Route  path={`/login`} component={LoginMUI} />

          { authenticated ?

          <App>

          <Route exact path='/' render={() => {
              return (<Redirect to={`/dashboard/`} />)
          }} /> 
